    /main>

    <
    Footer / >

    <
    EditProfilePopup isOpen = {
      isEditProfilePopupOpen
    }
    onClose = {
      closeAllPopups
    }
    onUpdateUser = {
      handleUpdateUser
    }
    />

    <
    EditAvatarPopup isOpen = {
      isEditAvatarPopupOpen
    }
    onClose = {
