import { useState, useEffect } from 'react';
import '../App.css';
import Header from './Header.js';
import Footer from './Footer.js';
import Main from './Main.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';
// Импортируем объект контекста
import { CurrentUserContext, defaultUserInfo } from '../contexts/CurrentUserContext.js';
import { api } from '../utils/Api.js';


function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isPreviewCardPopupOpen, setIsPreviewCardPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  // Переменная состояния пользователя
  const [currentUser, setCurrentUser] = useState(defaultUserInfo);


  //переменная состояния currentUser и эффект при монтировании, который будет вызывать api.getUserInfo и обновлять стейт-переменную из полученного значения
  useEffect(() => {
    api.getUserInfo()
        .then((data) => {
            setCurrentUser(data)

        })
        .catch((err) => { console.log(err) })
}, []);

  const handleEditProfileClick = () => {
    setIsEditProfilePopupOpen(true);
  }

  const handleAddPlaceClick = () => {
    setIsAddPlacePopupOpen(true);
  }

  const handleEditAvatarClick = () => {
    setIsEditAvatarPopupOpen(true);
  }

  const handleCardClick = (card) => {
    setIsPreviewCardPopupOpen(true);
    setSelectedCard(card)
  }

  const closeAllPopups = () => {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setIsPreviewCardPopupOpen(false);
    setSelectedCard({});
  };

  return (
    <>
      <CurrentUserContext.Provider value={currentUser}>
        <Header />
        <Main
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
        />
        <Footer />

        {/* <PopupWithForm
          name="profile"
          title="Редактировать профиль"
          textsubmit="Сохранить"
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}
        >
          <label className="popup__label">
            <input
              type="text"
              className="popup__input popup__input_type_name"
              name="name"
              id="input-name"
              placeholder="Имя"
              minLength={2}
              maxLength={40}
              required=""
            />
            <span className="input-name-error" />
          </label>
          <label className="popup__label">
            <input
              type="text"
              className="popup__input popup__input_type_description"
              name="about"
              id="input-about"
              placeholder="О себе"
              minLength={2}
              maxLength={200}
            />
            <span className="input-about-error" />
          </label>
        </PopupWithForm> */}

        <PopupWithForm
          name="place"
          title="Новое место"
          textsubmit="Сохранить"
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
        >
          <label className="popup__label">
            <input
              type="text"
              className="popup__input popup__input_type_title"
              name="name"
              placeholder="Название"
              minLength={2}
              maxLength={30}
              id="input-place-name"
              required=""
            />
            <span className="input-place-name-error" />
          </label>
          <label className="popup__label">
            <input
              type="url"
              className="popup__input popup__input_type_link"
              name="link"
              id="input-link"
              placeholder="Ссылка на картинку"
              required=""
            />
            <span className="input-link-error" />
          </label>
        </PopupWithForm>

        <PopupWithForm
          name="avatar"
          title="Обновить аватар"
          textsubmit="Сохранить"
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
        >
          <label className="popup__label">
            <input
              type="url"
              className="popup__input popup__input_type_link"
              name="avatar"
              id="input-avatar"
              placeholder="Ссылка на картинку"
              required=""
            />
            <span className="input-avatar-error" />
          </label>
        </PopupWithForm>

        <ImagePopup
          card={selectedCard}
          isOpen={isPreviewCardPopupOpen}
          onClose={closeAllPopups}
        />
      </CurrentUserContext.Provider>
    </>
  );
}

export default App;
