// import avatar from '../images/Photo-content/Avatar.jpg';
import { useEffect, useState, useContext } from 'react';
import { api } from '../utils/Api.js';
import Card from './Card.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';


function Main(props) {

  const { onEditProfile, onAddPlace, onEditAvatar, onCardClick } = props;

  // Подписываемся на контекст CurrentUserContext
  const currentUser = useContext(CurrentUserContext);
  // const [currentUser, setCurrentUser] = useState({ name: 'Юлия', about: 'Fox', avatar: avatar });
  const [cards, setCards] = useState([]);


  const cardComponents = cards.map((card) => (
    <Card
      key={card._id}
      card={card}
      onClick={onCardClick}
      onCardLike={handleCardLike}
    />
  ));

  useEffect(() => {
    Promise.all([api.getInitialCards()])
      .then((initialCards) => {
        // setCurrentUser(userData);
        setCards(initialCards);
      })
      .catch((err) => {
        console.log('//////Ошибка Promise.all//////', err);
      });
  }, [])

  // Функция постановки лайков карточке
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((serverCards) => serverCards.map((c) => c._id === card._id ? newCard : c));
      });
  }

    // Функция удаления карточки
    function handleCardDelete(card) {
      api.deleteCard(card._id)
        .then((deleteCard) => {
          setCards((serverCards) => serverCards.filter((c) => c._id !== card._id))
        })
    }


  return (
    <main className="content">
      <section className="profile">
        <button className="profile__avatar-button"
          onClick={onEditAvatar}
        >
          <img
            src={currentUser.avatar}
            alt="Аватарка"
            className="profile__avatar"
          />
        </button>
        <div className="profile__info">
          <div className="profile__name-edit-button">
            <h1 className="profile__name">{currentUser.name}</h1>
            <button
              className="button profile__edit-button"
              type="button"
              onClick={onEditProfile}
              aria-label="Редактировать"
            />
          </div>
          <p className="profile__description">{currentUser.about}</p>
        </div>
        <button
          type="button"
          className="button profile__add-button"
          aria-label="Добавить"
          onClick={onAddPlace}
        />
      </section>

      <section className="elements">
        <ul className="elements-list">{cardComponents}</ul>
      </section>

    </main>
  )
}

export default Main;
