import mesto__logo from '../images/Mesto__logo.svg';
import { Link, useLocation } from 'react-router-dom';
import React from 'react';

function Header(props) {
const {loggedIn, email, logOut} = props

const location = useLocation();
console.log('++++++++++!', location)

if(loggedIn){
  return (
  <header className="header">
    <img
      src={mesto__logo}
      alt="Лого Место"
      className="header__logo"
    />

    <div className="header__info">
      <p className="header__email">{email}</p>
      <Link to="" className="header__link link" onClo>Выйти</Link>
    </div>
  </header>
)}
if(location.pathname === '/sign-in') {
  return (
    <header className="header">
      <img
        src={mesto__logo}
        alt="Лого Место"
        className="header__logo"
      />

      <div className="header__info">
        <Link to="" className="header__link link">Регистрация</Link>
      </div>
    </header>
  )
}

return (
  <header className="header">
    <img
      src={mesto__logo}
      alt="Лого Место"
      className="header__logo"
    />


    <div className="header__info">
      <Link to="" className="header__link link">Войти</Link>
    </div>
  </header>
)
}
export default Header;
